import { Component } from '@angular/core';
import { Meta, Title } from '@angular/platform-browser'; 

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.css']
})
export class AboutComponent {

  urlDetails:any = {} ;
  error = '';
  pageDetails :any;
  constructor( private title:Title,private metaTagService: Meta,public tagService:TagService) {

  }   

  ngOnInit(): void {
    this.urlDetails.name = 'About Us';
      this.tagService.getTagDetais(this.urlDetails).subscribe(
        (res: any) => { 
          if(res){
            if(res.name !=''){
              this.title.setTitle(res.name);
            }
            if(res.title !=''){
              this.metaTagService.updateTag({ name: 'title', content:res.title}) 
            }
            if(res.keywords !=''){
              this.metaTagService.updateTag({ name: 'keywords', content: res.keywords }) 
            }
            if(res.description !=''){
              this.metaTagService.updateTag({ name: 'description', content: res.description }) 
            }
          }

        },
        (err) => (this.error = err.message)
      );

  }

}
